import React from 'react'
import { connect } from 'react-redux'
import { Link } from 'react-router-dom'
// import { getBlogs } from '../reducers/blogs'
import { Container, Image, Item, Button } from 'semantic-ui-react';
// import Blog from './Blog'
import BlogForm from './BlogForm'
import { getBlogs } from '../reducers/blogs'

class BlogList extends React.Component {
  // componentDidMount() {
  //   this.props.dispatch(getBlogs())
  // }

  state = { showForm: false, }

  toggleForm = () =>

  blogs = () => {
    const photograph = <Image src="http://lorempixel.com/640/480/fashion" />

    return this.props.blogs.map( blog =>
      <Item key={ blog.id }>
        <Item.Image src={ photograph } />

        <Item.Content verticalAlign='middle'>
          <Item.Header style={{ color: 'firebrick' }}>{ blog.title }</Item.Header>
          <Item.Extra>
            <Link to={`/blogs/${blog.id}`}>
              <Button inverted color='google plus' floated='right'>View Post</Button>
            </Link>
          </Item.Extra>
        </Item.Content>
      </Item>
    )
  }

  render() {
    // const { blogs } = this.props
    return (
      // <div>
      //   <ul>
      //     { blogs.map( blog => (
      //       <div key={blog.id}>
      //         {blog.editing ? <EditForm blog={blog} key={blog.id} /> :
      //         <Blog key={blog.id} blog={blog} /> }
      //       </div>
      //     ))}
      //   </ul>
      //   </div>
      <Container>
        <Item.Group divided>
          { this.blogs() }
        </Item.Group>
      </Container>
    )
  }
}

const mapStateToProps = (state) => {
  return { blogs: state.blogs, }
}

export default connect(mapStateToProps)(BlogList)
